mapper.update:
UPDATE R_HOUSE
   SET HOUSE_CODE = #{house_code}
     , EDUCATION_NAME = #{education_name}
     , EDUCATION_START = #{education_start}
     , EDUCATION_END = #{education_end}
     , UPDATE_DATE = NOW()
     , UPDATE_WRITER = #{writer}
 WHERE EDUCATION_NO = #{education_no}

UPDATE R_HOUSE
   SET DEL_YN = 'Y'
 WHERE EDUCATION_NO = #{education_no}

mapper.select:
SELECT t.*, @RNUM := @RNUM + 1 AS ROWNUM FROM ( SELECT R_EDU_NO AS r_edu_no , R_EDU_TITLE AS r_edu_title , DATE_FORMAT(R_EDU_START, '%Y-%m-%d') AS r_edu_start , DATE_FORMAT(R_EDU_END, '%Y-%m-%d') AS r_edu_end , R_EDU_PLACE AS r_edu_place , R_EDU_TEL AS r_edu_tel , R_EDU_SUMMARY AS r_edu_summary , CONVERT(R_EDU_CONTENTS USING utf8) AS r_edu_contents , ATTACHED_FILE_ID AS attached_file_id , CASE WHEN (SELECT USE_AT FROM R_COMM_FILE WHERE A.ATTACHED_FILE_ID = ATCH_FILE_ID) = 'Y' THEN 'Y' ELSE 'N' END AS attached_file_yn , (SELECT COUNT(*) FROM R_COMM_FILE_DETAIL WHERE A.ATTACHED_FILE_ID = ATCH_FILE_ID) AS attached_file_cnt , REGION_CODE AS region_code , (SELECT REGION_NAME FROM R_REGION_TABLE WHERE A.REGION_CODE = REGION_CODE) AS REGION_NAME , DATE_FORMAT(A.REG_DATE, '%Y-%m-%d') AS reg_date , A.WRITER AS writer , B.NAME AS name , A.DEL_YN AS del_yn FROM R_REGIONAL_EDU A LEFT JOIN R_ADMIN B ON A.WRITER = B.ID WHERE 1=1
AND A.R_EDU_TITLE LIKE CONCAT ('%', #{searchWrd},'%') OR A.R_EDU_CONTENTS LIKE CONCAT ('%', #{searchWrd},'%') OR A.WRITER LIKE CONCAT ('%', #{searchWrd},'%')
AND A.R_EDU_TITLE LIKE CONCAT ('%', #{searchWrd},'%')
AND A.R_EDU_CONTENTS LIKE CONCAT ('%', #{searchWrd},'%')
AND A.WRITER LIKE CONCAT ('%', #{searchWrd},'%')
AND A.REGION_CODE = #{region_code}
) t, ( SELECT @RNUM := 0 ) R WHERE t.DEL_YN = 'N' ORDER BY ROWNUM DESC LIMIT #{recordCountPerPage} OFFSET #{firstIndex}

SELECT R_EDU_NO AS r_edu_no , R_EDU_TITLE AS r_edu_title , DATE_FORMAT(R_EDU_START, '%Y-%m-%d') AS r_edu_start , DATE_FORMAT(R_EDU_END, '%Y-%m-%d') AS r_edu_end , R_EDU_PLACE AS r_edu_place , R_EDU_TEL AS r_edu_tel , R_EDU_SUMMARY AS r_edu_summary , CONVERT(R_EDU_CONTENTS USING utf8) AS r_edu_contents , ATTACHED_FILE_ID AS attached_file_id , CASE WHEN (SELECT USE_AT FROM R_COMM_FILE WHERE A.ATTACHED_FILE_ID = ATCH_FILE_ID) = 'Y' THEN 'Y' ELSE 'N' END AS attached_file_yn , (SELECT COUNT(*) FROM R_COMM_FILE_DETAIL WHERE A.ATTACHED_FILE_ID = ATCH_FILE_ID) AS attached_file_cnt , REGION_CODE AS region_code , (SELECT REGION_NAME FROM R_REGION_TABLE WHERE A.REGION_CODE = REGION_CODE) AS REGION_NAME , DATE_FORMAT(A.REG_DATE, '%Y-%m-%d') AS reg_date , A.WRITER AS writer , B.NAME AS name , A.DEL_YN AS del_yn , DISPLAY_YN AS display_yn
, (SELECT R_EDU_NO FROM R_REGIONAL_EDU WHERE R_EDU_NO < #{r_edu_no} AND DEL_YN = 'N' ORDER BY R_EDU_NO DESC LIMIT 1) AS prev_no , (SELECT R_EDU_TITLE FROM R_REGIONAL_EDU WHERE R_EDU_NO < #{r_edu_no} AND DEL_YN = 'N' ORDER BY R_EDU_NO DESC LIMIT 1) AS prev_title , (SELECT R_EDU_NO FROM R_REGIONAL_EDU WHERE R_EDU_NO > #{r_edu_no} AND DEL_YN = 'N' ORDER BY R_EDU_NO ASC LIMIT 1) AS next_no , (SELECT R_EDU_TITLE FROM R_REGIONAL_EDU WHERE R_EDU_NO > #{r_edu_no} AND DEL_YN = 'N' ORDER BY R_EDU_NO ASC LIMIT 1) AS next_title
FROM R_REGIONAL_EDU A LEFT JOIN R_ADMIN B ON A.WRITER = B.ID WHERE 1=1 AND R_EDU_NO = #{r_edu_no}

mapper.update:
UPDATE R_REGIONAL_EDU
   SET DEL_YN = 'Y'
 WHERE R_EDU_NO = #{r_edu_no}

UPDATE R_REGIONAL_EDU
   SET R_EDU_TITLE = #{r_edu_title} 
     , R_EDU_START = #{r_edu_start} 
     , R_EDU_END = #{r_edu_end}
     , R_EDU_PLACE = #{r_edu_place}
     , R_EDU_TEL = #{r_edu_tel}
     , R_EDU_SUMMARY = #{r_edu_summary}
     , R_EDU_CONTENTS = #{r_edu_contents}
     , REGION_CODE = #{region_code}

     , ATTACHED_FILE_ID = #{attached_file_id}

     , DISPLAY_YN = #{display_yn}
     , UPDATE_DATE = NOW()
     , UPDATE_WRITER = #{writer}
 WHERE R_EDU_NO = #{r_edu_no}
